import React, { Component } from 'react';
import Side from './sidebar';
import './Dashboard.css';
class Form extends Component(){
    constructor(props) {
        super(props);
        this.state ={
           form:{ 
            name:"",
            email:"",
        },
        formErrorMessage:{
        nameError:"",
        emailError:""
        },
        formValid:{
            name:false,
            email:false,
            buttonActive:false
        },
        errorMessage:"",
        successMessage:""
        };
    }
    handleSubmit = event => {
        event.preventDefault();
    }
    handleChange = event => {
          let {name,value} = event.target;
          let formDum = this.state.form;
          formDum[name]=value;
          this.setState({ form : formDum})
          this.validateField(name,value);
      }
    render(){
    return(
        <div className="dsh">
        <div className="dashboard">
        <div>
        <Side/>
        </div>
        <div className="content1">
        <form>
        <div className="form-inner">
        <h2> Hotel Booking </h2>
        <div className="form-group">
            <label>Name:</label>
            <input type="text" placeholder="Name"/>
        </div>
        <div className="form-group">
            <label>Email:</label>
            <input type="email" placeholder="someone@gmail.com"/>
        </div>
        <div className="form-group">
            <label>Room type:</label>
            <select>
                <option>Please select</option>
                <option>AC</option>
                <option>Non-AC</option>
            </select>
        </div>
        <div className="form-group">
            <label>Number of Guests:</label>
            <select>
                <option>Please select</option>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
            </select>
        </div>
        <div className="form-group">
            <label> From Date:</label>
            <input type="date" value="2021-09-27" min="2021-09-28" max="2021-11-30"/>
            
        </div>
        <div className="form-group">
        <label> To Date:</label>
            <input type="date" value="2021-09-27" min="2021-09-28" max="2021-11-30"/>
            </div>
            <div className="form-group">
                <button type="submit">Book</button>
                </div>
            </div>
        </form>
        </div>
        </div>
        </div>
    
    )
}
}
export default Form;
